  // POST /api/telnyx/noise-suppression - Toggle noise suppression for company
  app.post("/api/telnyx/noise-suppression", requireAuth, async (req: Request, res: Response) => {
    try {
      const user = req.user!;
      // Only admins can modify phone system settings
      if (user.role !== 'admin' && user.role !== 'superadmin') {
        return res.status(403).json({ message: "Forbidden - Only administrators can modify phone system settings" });
      }
      
      if (!user.companyId) {
        return res.status(400).json({ message: "No company associated with user" });
      }
      
      const { enabled, direction } = req.body;
      
      if (typeof enabled !== 'boolean') {
        return res.status(400).json({ message: "enabled must be a boolean" });
      }
      
      const validDirections = ['inbound', 'outbound', 'both'];
      if (direction && !validDirections.includes(direction)) {
        return res.status(400).json({ message: "direction must be 'inbound', 'outbound', or 'both'" });
      }
      
      // Get managed account config
      const config = await getManagedAccountConfig(user.companyId);
      if (!config) {
        return res.status(400).json({ message: "Phone system not configured. Please set up your phone system first." });
      }
      
      // Get credential connection ID from telephony settings
      const [settings] = await db
        .select({
          credentialConnectionId: telephonySettings.credentialConnectionId,
        })
        .from(telephonySettings)
        .where(eq(telephonySettings.companyId, user.companyId));
      
      if (!settings?.credentialConnectionId) {
        return res.status(400).json({ message: "No credential connection configured. Please complete phone system setup first." });
      }
      
      // Determine the noise_suppression value for Telnyx Voice API
      // CORRECT Options for Credential Connections: "disabled", "inbound", "outbound", "both"
      // Use direction value directly for Credential Connections
      let noiseSuppressionLevel = "disabled";
      if (enabled) {
        // Use direction value directly: disabled, inbound, outbound, both
        if (direction === "both") {
          noiseSuppressionLevel = "both";
        } else {
          noiseSuppressionLevel = direction;
        }
      }
      
      const TELNYX_API_BASE = "https://api.telnyx.com/v2";
      
      // CORRECT ENDPOINT: /v2/credential_connections/{id} NOT /v2/credential_connections/{id}
      // The noise_suppression field is at the ROOT level of the payload
      const payload = {
        noise_suppression: noiseSuppressionLevel,
      };
      
      console.log(`[Noise Suppression] Sending PATCH to credential_connections/${settings.credentialConnectionId}:`, JSON.stringify(payload));
      
      const response = await fetch(`${TELNYX_API_BASE}/credential_connections/${settings.credentialConnectionId}`, {
        method: "PATCH",
        headers: buildHeaders(config),
        body: JSON.stringify(payload),
      });
      if (!response.ok) {
        const errorText = await response.text();
        console.error(`[Noise Suppression] Failed to update credential connection: ${response.status} - ${errorText}`);
        return res.status(500).json({ 
          message: "Failed to update noise suppression in Telnyx",
          details: errorText 
        });
      }
      
      const responseData = await response.json();
      console.log(`[Noise Suppression] Updated credential connection ${settings.credentialConnectionId} - config: ${noiseSuppressionLevel}`);
      
      // Save settings to database
      await db.update(telephonySettings)
        .set({
          noiseSuppressionEnabled: enabled,
          noiseSuppressionDirection: direction || 'outbound',
          updatedAt: new Date(),
        })
        .where(eq(telephonySettings.companyId, user.companyId));
      
      res.json({
        success: true,
        enabled,
        direction: direction || 'outbound',
        telnyxConfig: noiseSuppressionLevel,
      });
    } catch (error: any) {
      console.error("[Noise Suppression] Update error:", error);
      res.status(500).json({ message: "Failed to update noise suppression settings" });
    }
  });


