  // POST /api/webrtc/check-balance - Check if user can make calls
  app.post("/api/webrtc/check-balance", requireAuth, async (req: Request, res: Response) => {
    try {
      const user = req.user!;
      
      if (!user.companyId) {
        return res.status(400).json({ canCall: false, message: "No company associated with user" });
      }

      const { getWalletByCompany } = await import("./services/wallet-service");
      const wallet = await getWalletByUser(user.companyId, user.id);
      
      const MINIMUM_BALANCE_FOR_CALLS = 0.50;
      const currentBalance = wallet ? parseFloat(wallet.balance) : 0;
      const canCall = currentBalance >= MINIMUM_BALANCE_FOR_CALLS;
      
      res.json({
        canCall,
        currentBalance: currentBalance.toFixed(2),
        minimumRequired: MINIMUM_BALANCE_FOR_CALLS.toFixed(2),
        message: canCall ? null : `Insufficient balance. Current: ${currentBalance.toFixed(2)}, Required: ${MINIMUM_BALANCE_FOR_CALLS.toFixed(2)}`,
      });
    } catch (error: any) {
      console.error("[WebRTC] Balance check error:", error);
      res.status(500).json({ canCall: false, message: "Failed to check balance" });
    }
  });



  // POST /api/webrtc/call-control-hangup - Hang up call using Call Control API with telnyxLegId
  // CRITICAL: The Telnyx WebRTC SDK has a BUG where hangup() ALWAYS sends 486 USER_BUSY
  // This is hardcoded in BaseCall.ts lines 386-387: cause: 'USER_BUSY', causeCode: 17
  // The only way to properly hangup with NORMAL_CLEARING (16) is via Call Control API
  // Per Telnyx Call Control API: https://developers.telnyx.com/docs/api/v2/call-control/Hangup-Call
  app.post("/api/webrtc/call-control-hangup", requireAuth, async (req: Request, res: Response) => {
    try {
      const user = req.user!;
      if (!user.companyId) {
        return res.status(400).json({ success: false, message: "No company associated" });
      }

      const { telnyxLegId } = req.body;

      // First, try to get the stored PSTN call_control_id from activeCallsMap
      // This is set by the Call Control webhook when calls come through the Call Control App
      const [credential] = await db
        .select({ sipUsername: telephonyCredentials.sipUsername })
        .from(telephonyCredentials)
        .where(
          and(
            eq(telephonyCredentials.companyId, user.companyId),
            eq(telephonyCredentials.isActive, true)
          )
        )
        .orderBy(desc(telephonyCredentials.lastUsedAt))
        .limit(1);

      let callControlIdToHangup = telnyxLegId;
      let dialedLegToHangup: string | undefined;

      if (credential?.sipUsername) {
        const activeCall = activeCallsMap.get(credential.sipUsername);
        if (activeCall?.callControlId) {
          console.log("[Call Control Hangup] Using stored PSTN call_control_id:", activeCall.callControlId);
          callControlIdToHangup = activeCall.callControlId;
          dialedLegToHangup = activeCall.dialedLegControlId;
        }
      }
      
      if (!callControlIdToHangup) {
        console.log("[Call Control Hangup] No callControlId available");
        return res.status(400).json({ success: false, message: "No call to hang up" });
      }

      console.log("[Call Control Hangup] Hanging up call via Call Control API:", { 
        callControlIdToHangup,
        dialedLegToHangup,
        providedTelnyxLegId: telnyxLegId,
        companyId: user.companyId
      });

      // Get API key from SecretsService
      const { SecretsService } = await import("./services/secrets-service");
      const secretsService = new SecretsService();
      let telnyxApiKey = await secretsService.getCredential("telnyx", "api_key");
      
      if (!telnyxApiKey) {
        console.error("[Call Control Hangup] Telnyx API key not configured");
        return res.status(500).json({ success: false, message: "Telnyx API key not configured" });
      }
      
      telnyxApiKey = telnyxApiKey.trim().replace(/[\r\n\t]/g, "");
      
      // Hang up both legs if available (PSTN and WebRTC legs)
      const hangupPromises: Promise<Response>[] = [];
      
      // Hang up the PSTN leg (primary)
      hangupPromises.push(fetch(
        `https://api.telnyx.com/v2/calls/${callControlIdToHangup}/actions/hangup`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${telnyxApiKey}`
          },
          body: JSON.stringify({})
        }
      ));

      // Also hang up the dialed leg (WebRTC SIP leg) if exists
      if (dialedLegToHangup && dialedLegToHangup !== callControlIdToHangup) {
        hangupPromises.push(fetch(
          `https://api.telnyx.com/v2/calls/${dialedLegToHangup}/actions/hangup`,
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              "Authorization": `Bearer ${telnyxApiKey}`
            },
            body: JSON.stringify({})
          }
        ));
      }

      const hangupResponses = await Promise.allSettled(hangupPromises);
      const primaryResult = hangupResponses[0];

      // Clean up activeCallsMap
      if (credential?.sipUsername) {
        activeCallsMap.delete(credential.sipUsername);
        callControlToSipMap.delete(callControlIdToHangup);
        if (dialedLegToHangup) {
          callControlToSipMap.delete(dialedLegToHangup);
        }
      }

      if (primaryResult.status === 'fulfilled' && primaryResult.value.ok) {
        console.log("[Call Control Hangup] Call terminated successfully:", callControlIdToHangup);
        return res.json({ success: true, message: "Call terminated via Call Control API" });
      } else if (primaryResult.status === 'fulfilled') {
        const errorText = await primaryResult.value.text();
        console.error("[Call Control Hangup] API error:", primaryResult.value.status, errorText);
        if (primaryResult.value.status === 404 || primaryResult.value.status === 422) {
          return res.json({ success: true, message: "Call already ended" });
        }
        return res.json({ success: false, message: "API error: " + primaryResult.value.status });
      } else {
        console.error("[Call Control Hangup] Request failed:", primaryResult.reason);
        return res.json({ success: false, message: "Request failed" });
      }
    } catch (error: any) {
      console.error("[Call Control Hangup] Error:", error);
      res.status(500).json({ success: false, message: error.message || "Hangup failed" });
    }
  });


  // POST /api/webrtc/server-hangup - Hang up PSTN call from server (avoids 486 Busy)
  // CRITICAL: This endpoint uses Telnyx Call Control API to terminate the PSTN leg cleanly
  // instead of relying on WebRTC SDK's hangup() which sends 486 Busy to the caller
  app.post("/api/webrtc/server-hangup", requireAuth, async (req: Request, res: Response) => {
    try {
      const user = req.user!;
      if (!user.companyId) {
        return res.status(400).json({ success: false, message: "No company associated" });
      }

      // Get the sipUsername for this company
      const [credential] = await db
        .select({ sipUsername: telephonyCredentials.sipUsername })
        .from(telephonyCredentials)
        .where(
          and(
            eq(telephonyCredentials.companyId, user.companyId),
            eq(telephonyCredentials.isActive, true)
          )
        )
        .orderBy(desc(telephonyCredentials.lastUsedAt))
        .limit(1);

      if (!credential?.sipUsername) {
        console.log("[WebRTC Server Hangup] No credential found for company:", user.companyId);
        return res.status(404).json({ success: false, message: "No WebRTC credential found" });
      }

      // Get the active call for this sipUsername
      const activeCall = activeCallsMap.get(credential.sipUsername);
      if (!activeCall) {
        console.log("[WebRTC Server Hangup] No active call found for sipUsername:", credential.sipUsername);
        // Even if no active call is tracked, respond success so client can clean up
        return res.json({ success: true, message: "No active call to hang up" });
      }

      console.log("[WebRTC Server Hangup] Hanging up PSTN call:", {
        sipUsername: credential.sipUsername,
        callSid: activeCall.callSid,
        from: activeCall.from,
        to: activeCall.to
      });

      // Use Telnyx Call Control API to hang up the PSTN leg
      try {
        // Get API key from SecretsService (stored in system_api_credentials table)
        const { SecretsService } = await import("./services/secrets-service");
        const secretsService = new SecretsService();
        let telnyxApiKey = await secretsService.getCredential("telnyx", "api_key");
        
        if (!telnyxApiKey) {
          console.error("[WebRTC Server Hangup] Telnyx API key not configured in system credentials");
          activeCallsMap.delete(credential.sipUsername);
          return res.json({ success: false, message: "Telnyx API key not configured" });
        }
        
        // Clean the API key
        telnyxApiKey = telnyxApiKey.trim().replace(/[\r\n\t]/g, "");
        console.log("[WebRTC Server Hangup] Using API key prefix:", telnyxApiKey.substring(0, 10));

        const hangupResponse = await fetch(
          `https://api.telnyx.com/v2/calls/${activeCall.callSid}/actions/hangup`,
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              "Authorization": `Bearer ${telnyxApiKey}`
            },
            body: JSON.stringify({})
          }
        );
        if (hangupResponse.ok) {
          console.log("[WebRTC Server Hangup] PSTN call terminated successfully:", activeCall.callSid);
          activeCallsMap.delete(credential.sipUsername);
          return res.json({ success: true, message: "PSTN call terminated" });
        } else {
          const errorText = await hangupResponse.text();
          console.error("[WebRTC Server Hangup] Telnyx API error:", hangupResponse.status, errorText);
          // Keep the call in activeCallsMap for retry
          return res.json({ success: false, message: "Telnyx API error: " + hangupResponse.status });
        }
      } catch (apiError: any) {
        console.error("[WebRTC Server Hangup] API call failed:", apiError);
        return res.json({ success: false, message: "API call failed: " + (apiError.message || "Unknown error") });
      }
    } catch (error: any) {
      console.error("[WebRTC Server Hangup] Error:", error);
      res.status(500).json({ success: false, message: error.message || "Hangup failed" });
    }
  });

    // POST /api/webrtc/call-log - Log WebRTC call events
  app.post("/api/webrtc/call-log", requireAuth, async (req: Request, res: Response) => {
    try {
      const user = req.user!;
      
      if (!user.companyId) {
        return res.status(400).json({ message: "No company associated with user" });
      }

      const { 
        callId,
        fromNumber, 
        toNumber, 
        direction, 
        status, 
        duration,
        startedAt,
        endedAt 
      } = req.body;

      if (!fromNumber || !toNumber || !direction || !status) {
        return res.status(400).json({ message: "Missing required fields" });
      }

      // Check if call already exists (update) or create new
      const existingLog = callId ? await db.query.callLogs.findFirst({
        where: and(
          eq(callLogs.telnyxCallId, callId),
          eq(callLogs.companyId, user.companyId)
        )
      }) : null;

      if (existingLog) {
        // Update existing call log
        await db.update(callLogs)
          .set({
            status,
            duration: duration || 0,
            endedAt: endedAt ? new Date(endedAt) : new Date(),
          })
          .where(eq(callLogs.id, existingLog.id));
        
        console.log("[WebRTC] Updated call log:", existingLog.id, "status:", status);
        return res.json({ success: true, id: existingLog.id, updated: true });
      }

      // Try to match contact by phone number - handle null phones safely
      const normalizedFrom = fromNumber.replace(/\D/g, '').slice(-10);
      const normalizedTo = toNumber.replace(/\D/g, '').slice(-10);
      
      // Use the remote party's number for contact matching
      const phoneToMatch = direction === 'inbound' ? normalizedFrom : normalizedTo;
      
      let matchedContact: any = null;
      try {
        matchedContact = await db.query.contacts.findFirst({
          where: and(
            eq(contacts.companyId, user.companyId),
            isNotNull(contacts.phone),
            sql`RIGHT(REGEXP_REPLACE(phone, '[^0-9]', '', 'g'), 10) = ${phoneToMatch}`
          )
        });
      } catch (contactError) {
        console.log("[WebRTC] Contact lookup failed, proceeding without contact:", contactError);
      }

      console.log("[WebRTC] Creating call log:", { 
        fromNumber, toNumber, direction, status, 
        matchedContactId: matchedContact?.id,
        phoneToMatch
      });

      // Create new call log - ensure all values are properly defined
      const insertValues: any = {
        companyId: user.companyId,
        userId: user.id,
        telnyxCallId: callId || `webrtc-${Date.now()}`,
        fromNumber: fromNumber,
        toNumber: toNumber,
        direction: direction,
        status: status,
        duration: duration || 0,
        startedAt: startedAt ? new Date(startedAt) : new Date(),
      };
      
      // Only add contactId and callerName if we have a match
      if (matchedContact?.id) {
        insertValues.contactId = matchedContact.id;
        const callerName = `${matchedContact.firstName || ''} ${matchedContact.lastName || ''}`.trim();
        if (callerName) {
          insertValues.callerName = callerName;
        }
      }

      const [newLog] = await db.insert(callLogs).values(insertValues).returning();

      console.log("[WebRTC] Created call log:", newLog.id, "direction:", direction, "to:", toNumber);
      res.json({ success: true, id: newLog.id, created: true });
    } catch (error: any) {
      console.error("[WebRTC] Call log error:", error);
      res.status(500).json({ message: "Failed to log call" });
    }
  });

